    @Test
    public void testJSONIn(){
        ObjectNode result = Json.newObject();
        JsonNode node = Json.parse(testJson);
        ArrayNode spaces = (ArrayNode)node.get("board").get("spaces");
        Board b = new Board();
        for (int i = 0; i < spaces.size(); i++){
            ArrayNode row = (ArrayNode) spaces.get(i);
            for (int j = 0; j < row.size(); j++){
                if (!row.get(j).get("value").isNull()) {
                    Character c = (Character) row.get(j).get("value").textValue().toCharArray()[0];
                    b.getSpace(i, j).setValue(c);
                }
            }
        }
        p(b);
        //p(results.size());
        //ArrayList<ArrayList<BoardSpace>> ss = Json.fromJson(node.get("board"), ArrayList.class);
        //GameModel g = Json.fromJson(node, GameModel.class);
        //p(Json.toJson(g));
    }

